import { useState } from "react";
import { IArticle } from "../utils/interface/article";
import { Image, Typography, Modal, Row, Col } from "antd";
const { Title, Text, Paragraph } = Typography;
const options: Intl.DateTimeFormatOptions = {
  weekday: "long",
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "2-digit",
  minute: "2-digit",
};

interface IArticleProps {
  article: IArticle;
}

export const CardAricle = (props: IArticleProps) => {
  const { article } = props;
  const { title, publishedAt, author, urlToImage, description } = article;
  const [detailArticles, setDetailArticles] = useState<IArticle>();
  const [open, setOpen] = useState(false);
  const openModal = (article: IArticle) => {
    setOpen(true);
    setDetailArticles(article);
  };
  return (
    <>
      <div onClick={() => openModal(article)} style={{ cursor: "pointer" }}>
        <div className="cardImage">
          <Image
            preview={false}
            width="100%"
            alt={title}
            src={urlToImage}
            fallback="data:image/png;base64,"
          />
        </div>
        <div style={{ marginTop: "8px" }}>
          <Text type="secondary">
            {new Date(publishedAt).toLocaleString("id", options)}
          </Text>
          <Text type="secondary">{author ? `, Penulis : ${author}` : ""}</Text>
          <Title level={4} style={{ marginTop: "0.4em" }}>
            {title}
          </Title>
          <Paragraph>{description}</Paragraph>
        </div>
      </div>

      <Modal
        title="Detail Article"
        centered
        open={open}
        onCancel={() => setOpen(false)}
        footer={false}
        width={1000}
      >
        <Row gutter={24}>
          <Col className="gutter-row" span={12}>
            <Image
              preview={false}
              width="100%"
              alt={detailArticles?.title}
              src={detailArticles?.urlToImage}
              fallback="data:image/png;base64,"
            />
          </Col>
          <Col className="gutter-row" span={12}>
            <div>
              <Title level={4}>{detailArticles?.title}</Title>
              <Text type="secondary">
                {new Date(
                  detailArticles ? detailArticles.publishedAt : ""
                ).toLocaleString("id", options)}
              </Text>
              <Text type="secondary">
                {detailArticles?.author
                  ? `, Penulis : ${detailArticles?.author}`
                  : ""}
              </Text>
              <Paragraph>{detailArticles?.description}</Paragraph>
              <Paragraph>
                {detailArticles?.content}{" "}
                <a href={detailArticles?.url} target="_blank">
                  more...
                </a>
              </Paragraph>
            </div>
          </Col>
        </Row>
      </Modal>
    </>
  );
};
